// components/StudentList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Modal, Form } from 'react-bootstrap';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [faculties, setFaculties] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [newStudent, setNewStudent] = useState({
    studentCode: '',
    fullName: '',
    address: '',
    facultyID: '', // Add facultyID field for the association with Faculty
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const studentsResponse = await axios.get('http://localhost:5235/api/student');
      const facultiesResponse = await axios.get('http://localhost:5235/api/faculty');
      setStudents(studentsResponse.data);
      setFaculties(facultiesResponse.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleShowAddModal = () => {
    setShowAddModal(true);
  };

  const handleCloseAddModal = () => {
    setShowAddModal(false);
  };

  const handleShowEditModal = (student) => {
    setSelectedStudent(student);
    setShowEditModal(true);
  };

  const handleCloseEditModal = () => {
    setSelectedStudent(null);
    setShowEditModal(false);
  };

  const handleDelete = async (studentID) => {
    try {
      await axios.delete(`http://localhost:5235/api/student/${studentID}`);
      fetchData(); // Refresh the student list after deleting a student
    } catch (error) {
      console.error(error);
    }
  };

  const handleInputChange = (e) => {
    setNewStudent({ ...newStudent, [e.target.name]: e.target.value });
  };

  const handleAddStudent = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5235/api/student', newStudent);
      handleCloseAddModal(); // Close the modal after adding a new student
      fetchData(); // Refresh the student list after adding a new student
    } catch (error) {
      console.error(error);
    }
  };

  const handleUpdateStudent = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5235/api/student/${selectedStudent.studentID}`, selectedStudent);
      handleCloseEditModal(); // Close the modal after editing the student
      fetchData(); // Refresh the student list after editing
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between mb-2">
        <Button variant="primary" onClick={handleShowAddModal}>
          Add Student
        </Button>
      </div>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Student Code</th>
            <th>Full Name</th>
            <th>Address</th>
            <th>Faculty</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.studentID}>
              <td>{student.studentCode}</td>
              <td>{student.fullName}</td>
              <td>{student.address}</td>
              <td>{student.faculty?.facultyName}</td>
              <td>
                <Button variant="info" onClick={() => handleShowEditModal(student)}>
                  Edit
                </Button>{' '}
                <Button variant="danger" onClick={() => handleDelete(student.studentID)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showAddModal} onHide={handleCloseAddModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Student</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleAddStudent}>
            <Form.Group className="mb-3">
              <Form.Label>Student Code:</Form.Label>
              <Form.Control type="text" name="studentCode" onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Full Name:</Form.Label>
              <Form.Control type="text" name="fullName" onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Address:</Form.Label>
              <Form.Control type="text" name="address" onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Faculty:</Form.Label>
              <Form.Select name="facultyID" onChange={handleInputChange}>
                <option value="">Select Faculty</option>
                {faculties.map((faculty) => (
                  <option key={faculty.facultyID} value={faculty.facultyID}>
                    {faculty.facultyName}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            <Button variant="primary" type="submit">
              Add
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      <Modal show={showEditModal} onHide={handleCloseEditModal}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Student</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleUpdateStudent}>
            <Form.Group className="mb-3">
              <Form.Label>Student Code:</Form.Label>
              <Form.Control type="text" name="studentCode" value={selectedStudent?.studentCode} readOnly />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Full Name:</Form.Label>
              <Form.Control type="text" name="fullName" value={selectedStudent?.fullName} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Address:</Form.Label>
              <Form.Control type="text" name="address" value={selectedStudent?.address} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Faculty:</Form.Label>
              <Form.Select name="facultyID" value={selectedStudent?.facultyID} onChange={handleInputChange}>
                <option value="">Select Faculty</option>
                {faculties.map((faculty) => (
                  <option key={faculty.facultyID} value={faculty.facultyID}>
                    {faculty.facultyName}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            <Button variant="primary" type="submit">
              Save Changes
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default StudentList;
