import React, { useState } from 'react';
import axios from 'axios';

const AddPerson = () => {
  const [newPerson, setNewPerson] = useState({
    studentID: '',
    studentCode: '',
    fullName: '',
    age: '',
    address: '',
    email: ''
  });

  const handleInputChange = e => {
    setNewPerson({ ...newPerson, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5235/api/persons', newPerson);
      // Refresh the person list after adding a new person
      // You can also redirect to the list page or show a success message
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-3">
      <h2>Add Person</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Student ID:</label>
          <input type="text" className="form-control" name="studentID" onChange={handleInputChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Student Code:</label>
          <input type="text" className="form-control" name="studentCode" onChange={handleInputChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">FullName:</label>
          <input type="text" className="form-control" name="fullName" onChange={handleInputChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Age:</label>
          <input type="text" className="form-control" name="age" onChange={handleInputChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Address:</label>
          <input type="text" className="form-control" name="address" onChange={handleInputChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Email:</label>
          <input type="text" className="form-control" name="email" onChange={handleInputChange} />
        </div>
        <button type="submit" className="btn btn-primary">Add</button>
      </form>
    </div>
  );
};

export default AddPerson;
